package hvcntt.org.shoppingweb.dao.entity;import hvcntt.org.shoppingweb.dao.dto.ProductSize;import javax.persistence.*;import java.io.Serializable;/** * Created by Nguyen on 09/04/2017. */@Entity@Table(name = "detailproduct")public class DetailProduct implements Serializable {    private static final long serialVersionUID = -4282371784999553906L;    @Id    @GeneratedValue    @Column(name = "product_detail_id")    private String detailProductId;    @Column(name = "detail_size")    private ProductSize productSize;    @Column(name = "detail_color")    private String productColor;    @ManyToOne(targetEntity = Product.class, cascade = CascadeType.ALL, fetch = FetchType.EAGER)    @JoinColumn(name = "product_id")    private Product product;    public DetailProduct() {    }    public DetailProduct(ProductSize productSize, String productColor, Product product) {        this.productSize = productSize;        this.productColor = productColor;        this.product = product;    }    public String getDetailProductId() {        return detailProductId;    }    public void setDetailProductId(String detailProductId) {        this.detailProductId = detailProductId;    }    public ProductSize getProductSize() {        return productSize;    }    public void setProductSize(ProductSize productSize) {        this.productSize = productSize;    }    public String getProductColor() {        return productColor;    }    public void setProductColor(String productColor) {        this.productColor = productColor;    }    public Product getProduct() {        return product;    }    public void setProduct(Product product) {        this.product = product;    }}