package hvcntt.org.shoppingweb.dao.entity;import javax.persistence.*;import java.io.Serializable;import java.util.List;import java.util.Set;import java.util.UUID;/** * Created by Nguyen on 09/04/2017. */@Entity@Table(name = "producttype")public class ProductType implements Serializable {    private static final long serialVersionUID = 7185369628203377518L;    @Id    @GeneratedValue    @Column(name = "type_id")    private String typeId = UUID.randomUUID().toString();    @Column(name = "type_name")    private String typeName;    @OneToMany(fetch = FetchType.EAGER, mappedBy = "productType",targetEntity = Product.class)    private Set<Product> products;    public ProductType() {    }    public ProductType(String typeName, Set<Product> products) {        this.typeName = typeName;        this.products = products;    }    public String getTypeId() {        return typeId;    }    public void setTypeId(String typeId) {        this.typeId = typeId;    }    public String getTypeName() {        return typeName;    }    public void setTypeName(String typeName) {        this.typeName = typeName;    }    public Set<Product> getProducts() {        return products;    }    public void setProducts(Set<Product> products) {        this.products = products;    }}