package hvcntt.org.shoppingweb.dao.entity;import javax.persistence.*;import java.io.Serializable;import java.util.UUID;/** * Created by Nguyen on 09/04/2017. */@Entity@Table(name = "supplier")public class Supplier implements Serializable {    private static final long serialVersionUID = -6300109596031222689L;    @Id    @GeneratedValue    @Column(name = "supplier_id")    private String supplierId = UUID.randomUUID().toString();    @Column(name = "supplier_name")    private String supllierName;    public Supplier() {    }    public Supplier(String supllierName) {        this.supllierName = supllierName;    }    public String getSupplierId() {        return supplierId;    }    public void setSupplierId(String supplierId) {        this.supplierId = supplierId;    }    public String getSupllierName() {        return supllierName;    }    public void setSupllierName(String supllierName) {        this.supllierName = supllierName;    }}