package hvcntt.org.shoppingweb.dao.entity;import javax.persistence.*;import java.io.Serializable;import java.util.Date;import java.util.UUID;@Entity@Table(name="user_attempts")@NamedQuery(name="UserAttempt.findAll", query="SELECT u FROM UserAttempt u")public class UserAttempt implements Serializable {    private static final long serialVersionUID = 8657479744951400373L;    @Id    @Column(name="iduserattempts")    private String userAttemptsId;    private int attempts = 0;    private Date lastModified;    @Column(name="username")    private String username;    public UserAttempt() {        setUserAttemptsId(UUID.randomUUID().toString());    }    public UserAttempt(String username, int attempts, Date lastModified) {        this.username = username;        this.attempts = attempts;        this.lastModified = lastModified;    }    public int getAttempts() {        return this.attempts;    }    public void setAttempts(int attempts) {        this.attempts = attempts;    }    public Date getLastModified() {        return this.lastModified;    }    public void setLastModified(Date lastModified) {        this.lastModified = lastModified;    }    public String getUsername() {        return username;    }    public void setUsername(String username) {        this.username = username;    }    public String getUserAttemptsId() {        return userAttemptsId;    }    public void setUserAttemptsId(String userAttemptsId) {        this.userAttemptsId = userAttemptsId;    }}