package hvcntt.org.shoppingweb.dao.repository;import hvcntt.org.shoppingweb.dao.dto.AuctionStatus;import hvcntt.org.shoppingweb.dao.entity.Auction;import hvcntt.org.shoppingweb.dao.entity.Product;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.data.jpa.repository.Modifying;import org.springframework.data.jpa.repository.Query;import org.springframework.data.repository.query.Param;public interface AuctionRepository extends JpaRepository<Auction, String> {    Auction findByProduct(Product product);    Auction findByProductAndAuctionStatus(Product product, AuctionStatus auctionStatus);    @Modifying    @Query("UPDATE Auction a SET a.auctionStatus = :auctionStatus WHERE a.product = :product")    void updateAuctionByProductAndAuctionStatusIsAuctioning(@Param("product") Product product, @Param("auctionStatus") AuctionStatus auctionStatus);}