package hvcntt.org.shoppingweb.dao.repository;import hvcntt.org.shoppingweb.dao.entity.Category;import hvcntt.org.shoppingweb.dao.entity.Parent;import org.springframework.data.jpa.repository.JpaRepository;import java.util.List;public interface CategoryRepository extends JpaRepository<Category, String> {    Category findByCategoryName(String categoryName);    List<Category> findByParent(Parent parent);}