package hvcntt.org.shoppingweb.dao.repository;import java.util.List;import javax.transaction.Transactional;import hvcntt.org.shoppingweb.dao.entity.Product;import hvcntt.org.shoppingweb.dao.entity.TransactionType;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.data.jpa.repository.Modifying;import org.springframework.data.jpa.repository.Query;import org.springframework.data.repository.query.Param;public interface ProductRepository extends JpaRepository<Product, String> {	List<Product> findByNameContaining(String name);	@Modifying	@Transactional	@Query("update Product p set p.viewNumber = p.viewNumber + 1 where p.productId = :productId")	void updateView(@Param("productId") String idproduct);	List<Product> findByTransactionType(TransactionType transactionType);}